// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./YiDengToken.sol";

/**
 * @title ArticleMarket
 * @notice 一灯教育文章市场合约
 */
contract ArticleMarket is Ownable {
    // 合约实例
    YiDengToken public yiDengToken;

    // 文章结构体
    struct Article {
        string web2ArticleId; // Web2平台的文章ID
        string title; // 文章标题
        address author; // 文章作者地址
        bool isActive; // 文章是否有效
        uint256 totalTips; // 文章收到的总打赏金额(YD代币)
    }

    // 合约状态变量
    mapping(uint256 => Article) public articles; // articleId => Article
    mapping(string => uint256) public web2ToArticleId; // web2ArticleId => articleId
    mapping(address => uint256[]) public authorArticles; // author => 发布的文章ID列表
    mapping(address => mapping(uint256 => bool)) public tippedArticles; // tipper => articleId => 是否打赏过
    mapping(address => uint256[]) public userTippedArticleIds; // tipper => 打赏过的文章ID列表
    uint256 public articleCount; // 文章总数

    // 事件
    event ArticlePublished(
        uint256 indexed articleId,
        string web2ArticleId,
        string title,
        address indexed author
    );
    event ArticleTipped(
        address indexed tipper,
        uint256 indexed articleId,
        uint256 amount,
        address indexed author
    );

    constructor(address _tokenAddress) {
        yiDengToken = YiDengToken(payable(_tokenAddress));
    }

    function publishArticle(string calldata web2ArticleId, string calldata title) external {
        require(bytes(web2ArticleId).length > 0, "Web2 article ID cannot be empty");
        require(web2ToArticleId[web2ArticleId] == 0, "Article already exists");
        require(bytes(title).length > 0, "Title cannot be empty");

        articleCount++;

        articles[articleCount] = Article({
            web2ArticleId: web2ArticleId,
            title: title,
            author: msg.sender,
            isActive: true,
            totalTips: 0
        });

        web2ToArticleId[web2ArticleId] = articleCount;
        authorArticles[msg.sender].push(articleCount); // 记录作者发布的文章ID

        emit ArticlePublished(articleCount, web2ArticleId, title, msg.sender);
    }

    function tipArticle(string calldata web2ArticleId, uint256 amount) external {
        uint256 articleId = web2ToArticleId[web2ArticleId];
        require(articleId > 0, "Article does not exist");

        Article storage article = articles[articleId];
        require(article.isActive, "Article not active");
        require(amount > 0, "Tip amount must be greater than 0");
        require(article.author != msg.sender, "Cannot tip your own article");

        require(yiDengToken.transferFrom(msg.sender, article.author, amount), "Transfer failed");

        article.totalTips += amount;

        // 记录打赏信息
        if (!tippedArticles[msg.sender][articleId]) {
            tippedArticles[msg.sender][articleId] = true;
            userTippedArticleIds[msg.sender].push(articleId);
        }

        emit ArticleTipped(msg.sender, articleId, amount, article.author);
    }

    function setArticleStatus(string calldata web2ArticleId, bool isActive) external onlyOwner {
        uint256 articleId = web2ToArticleId[web2ArticleId];
        require(articleId > 0, "Article does not exist");
        articles[articleId].isActive = isActive;
    }

    function getArticleInfo(string calldata web2ArticleId)
        external view returns (string memory, address, bool, uint256) {
        uint256 articleId = web2ToArticleId[web2ArticleId];
        require(articleId > 0, "Article does not exist");
        Article memory article = articles[articleId];
        return (article.title, article.author, article.isActive, article.totalTips);
    }

    function articleExists(string calldata web2ArticleId) external view returns (bool) {
        return web2ToArticleId[web2ArticleId] > 0;
    }

    /**
     * @notice 获取用户发布的文章ID列表
     * @param author 作者地址
     * @return uint256[] 文章ID数组
     */
    function getAuthorArticleIds(address author) external view returns (uint256[] memory) {
        return authorArticles[author];
    }

    /**
     * @notice 获取用户打赏过的文章ID列表
     * @param tipper 打赏者地址
     * @return uint256[] 文章ID数组
     */
    function getUserTippedArticleIds(address tipper) external view returns (uint256[] memory) {
        return userTippedArticleIds[tipper];
    }
}

